from xbmctorrentV2 import plugin, magnet, scrapers
from xbmctorrentV2.ga import tracked
from xbmctorrentV2.utils import ensure_fanart
from xbmctorrentV2.platform import PLATFORM
from xbmctorrentV2.scrapers import \
    MODULES, \
    eztv, \
    yify, \
    kickass, \
    tpb, \
    btdigg, \
    bitsnoop, \
    extratorrent, \
    nyaa


@plugin.route("/")
@ensure_fanart
@tracked(force=True)
def index():
    if PLATFORM["os"] not in ["android", "linux", "windows", "darwin"]:
        plugin.notify("Your system \"%(os)s_%(arch)s\" is not supported." % PLATFORM, delay=15000)

    for module in MODULES:
        yield {
            "label": module["name"],
            "thumbnail": module["image"],
            "path": plugin.url_for(module["view"]),
        }
